import save from '../../../images/saved.svg'
import { CurrentUserContext } from '../../../context/CurrentUserContext';
import { useContext } from 'react';

function MoviesCard({title, time, poster, onCardLike, movie}) {
    const currentUser = useContext(CurrentUserContext);

    const isLiked = movie.likes.some((i) => i === currentUser._id);
    const cardLikeButtonClassName = `button card__button ${isLiked && 'card__button_click'}`;

    const handleLikeClick = () => {
        onCardLike(movie);
    };

    return(
        <article className='card'>
            <h3 className='card__title'>{title}</h3>
            <p className='card__time'>{time}</p>
            <button className={cardLikeButtonClassName} type="button" onClick={handleLikeClick}></button>
            <img src={poster} alt={title} className='card__img' />
        </article>
    )
}

export default MoviesCard;
