import logo from '../../images/logo.svg';
import React from 'react';
import { Link } from 'react-router-dom';

function Login({ onLogin }) {

    const [userData, setUserData] = React.useState({ name: '', email: '', password: '' });

    const handleChange = (e) => {
      const { name, value } = e.target;
      setUserData((evt) => ({
        ...evt,
        [name]: value,
      }));
    }

    const handleSubmit = (e) => {
      e.preventDefault();
      const { name, email, password } = userData
      onLogin(name, email, password)

    }


    return (
        <login className='login'>
            <Link className='button header__logo-btn sign__logo' to='/'>
                <img src={logo} alt='Логотип.' className='header__logo' />
            </Link>

            <h1 className='sign__title'>Рады видеть!</h1>

            <form className="sign__form" onSubmit={handleSubmit}>
                <input className="sign__input popup__item" id="email" name="email" type="email" placeholder="Email" value={userData.email || ''} onChange={handleChange} required />
                <input className="sign__input popup__item" id="password" name="password" type="password" placeholder="Пароль" value={userData.password || ''} onChange={handleChange} required />
                <div className="sign__button-container">
                    <button type="submit" className="button popup__button sign__link">Войти</button>
                </div>
            </form>

            <div className="sign__signup">
                <p className="sign__signup-text">Ещё не зарегистрированы?</p>&nbsp;
                <Link to="/signup" className="sign__signup-link">Регистрация</Link>
            </div>
        </login>

    )
}

export default Login
