import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import burger from '../../../images/burger.svg';
import close from '../../../images/Close.svg';

function NavTab() {
    const [navbarOpen, setNavbarOpen] = useState(false);

    const handleToggle = () => {
        setNavbarOpen(!navbarOpen)
    }
    const closeMenu = () => {
        setNavbarOpen(false)
    }
    return (
        <navTab className='navBar'>
            <button onClick={handleToggle} className='button'>{navbarOpen ? <img src={close} alt='close' className='burger__close burger__button' />: <img src={burger} alt='burger' className='burger__open burger__button' />}</button>
            <div className={`menuNav ${navbarOpen ? " showMenu" : ""}`}>
               <NavLink
                    to='/'
                    activeClassName="active-link"
                    onClick={() => closeMenu()}
                    exact
                >
                    Hello
                </NavLink>
            </div>
        </navTab>
    )
};

export default NavTab;
