import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import burger from '../../../images/burger.svg';
import close from '../../../images/Close.svg';

function NavTab() {
    const [navbarOpen, setNavbarOpen] = useState(false);

    const handleToggle = () => {
        setNavbarOpen(!navbarOpen)
    }
    const handleToggleReverse =() => {
        setNavbarOpen(false)
    }
    const closeMenu = () => {
        setNavbarOpen(false)
    }
    return (
        <navTab className='burger'>
            <button onClick={handleToggle} className='button burger__button_open'><img src={burger} alt='burger' className='burger__open' /></button>
            <div className={`burger__menu ${navbarOpen ? "burger__show" : ""}`}>
                <button onClick={handleToggleReverse} className='button burger__button_close'><img src={close} alt='close' className='burger__close burger__button' /></button>
               <NavLink
                    to='/'
                    activeClassName="active-link"
                    onClick={() => closeMenu()}
                    exact
                >
                    Hello
                </NavLink>
            </div>
        </navTab>
    )
};

export default NavTab;
