import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';
import burger from '../../../images/burger.svg';
import close from '../../../images/Close.svg';

function NavTab() {
    const [menuOpen, setMenuOpen] = useState(false);

    const handleToggle = () => {
        setMenuOpen(!menuOpen)
    }
    const closeMenu = () => {
        setMenuOpen(false)
    }
    return (
        <navTab className='burger'>
            <button onClick={handleToggle} className='button burger__button_open'><img src={burger} alt='burger' className='burger__open' /></button>
            <div className={`burger__menu ${menuOpen ? 'burger__show' : ''}`}>
                <div className='burger__header-menu'>
                    <button onClick={closeMenu} className='button burger__button_close'>
                        {/* <img src={close} alt='close' className='burger__close burger__button' /> */}
                    </button>
                </div>
                <NavLink
                    to='/'
                    activeClassName="active-link"
                    onClick={() => closeMenu()}
                    exact
                >
                    Hello
                </NavLink>
            </div>
        </navTab>
    )
};

export default NavTab;
